import "./SignUp.css";
import { useState } from "react";
import axios from "axios";

const SERVER_URL = 'https://mapdagu.site/api/sign-up/email';

const SignUp = ({onSubmit}) => {
    const [initInput, setInitInput] = useState(false);
    const [isSended, setIsSended] = useState(false);
    const [isCodeChecked, setIsCodeChecked] = useState(false);
    const [response, setResponse] = useState();

    const [inputValue, setInputValue] = useState({
        nickname: "",
        email: "",
        checkedEmail: "",
        code: "",
        password: "",
        passwordConfirm: "",
    });
    const { nickname, email, checkedEmail, code, password, passwordConfirm } = inputValue;
    //Ïù¥Î¶Ñ 2Í∏ÄÏûê Ïù¥ÏÉÅ 5Í∏ÄÏûê Ïù¥Ìïò
    const isValidName = nickname.length >= 2 && nickname.length <= 5;
    // Ïù¥Î©îÏùº Í≤ÄÏÇ¨: '@', '.'Ïù¥ Îëò Îã§ Ìè¨Ìï®
    const isValidEmail1 = email.includes('@') && email.includes('.');
    const isValidEmail2 = checkedEmail.includes('@') && checkedEmail.includes('.');
    // ÎπÑÎ∞ÄÎ≤àÌò∏ 8Ïûê Ïù¥ÏÉÅ
    const isValidPassword = password.length >= 8;
    // ÎπÑÎ∞ÄÎ≤àÌò∏ÏôÄ ÎπÑÎ∞ÄÎ≤àÌò∏ ÌôïÏù∏ Í∞íÏù¥ ÏùºÏπòÌï† Í≤É
    const isPasswordChecked = ((password === passwordConfirm) ? true : false);

    const handleInput = (e) => {
        const { name, value } = e.target;
        setInputValue({
            ...inputValue,
            [name]: value,
        });
        setInitInput(false);
    };
    
    const sendCode = async (e) => {
        if(isValidEmail1){
            setIsSended(true);
            setResponse((await axios.post(SERVER_URL, {email})).data.authCode);
        }
        setInputValue({
            ...inputValue,
            checkedEmail: email,
        })
        setInitInput(true);
    }
    const checkCode = () => {
        if(code === response){
            setIsCodeChecked(true);
        }
        else{
            setIsCodeChecked(false);
            alert("Ïù∏Ï¶ùÎ≤àÌò∏Î•º Îã§Ïãú ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî");
        }
    }
    const getIsActive = 
    isValidName && isValidEmail2 && isCodeChecked && isValidPassword && isPasswordChecked === true;

    const onSubmitHandler = () => {
        if(getIsActive){
            onSubmit(inputValue);
        }        
        else{
            alert("Ìï≠Î™©ÏùÑ Ï±ÑÏõåÏ£ºÏÑ∏Ïöî");
        }
    }

    return (
        <div className="SignUp">
            <h1>ÌöåÏõêÍ∞ÄÏûÖ</h1>
            <div className="signUpInput">
                <div className="container">
                    <div className="inputMessage">Ïù¥Î¶Ñ</div>
                    <input 
                        name="nickname"
                        onChange={handleInput}
                    />
                    <h5>{(nickname.length!==0 && !isValidName) ? '2Í∏ÄÏûê Ïù¥ÏÉÅ 5Í∏ÄÏûê Ïù¥ÌïòÎ°ú ÏûÖÎ†•Ìï¥ Ï£ºÏÑ∏Ïöîüò¢' : ''}</h5>
                </div>

                <div className="container">
                    <div className="inputMessage">Ïù¥Î©îÏùº</div>
                    <div className="wrapper">
                        <input 
                            name="email"
                            onChange={handleInput}
                        />
                        <button className="btn_type1" onClick={sendCode}>Ïù∏Ï¶ùÎ≤àÌò∏ Ï†ÑÏÜ°</button>   
                    </div>
                    <h5>{(email.length!==0 && !isValidEmail1 && !isValidEmail2) ? 'Ïù¥Î©îÏùº ÌòïÏãùÏù¥ ÌãÄÎ†∏Ïñ¥Ïöîüò¢' : ''}</h5>
                    <h6>{(isSended && isValidEmail2) ? 'Ïù∏Ï¶ùÎ≤àÌò∏Í∞Ä Ï†ÑÏÜ°ÎêòÏóàÏñ¥Ïöî' : ''}</h6>
                
                    <div className="inputMessage">Ïù∏Ï¶ùÎ≤àÌò∏</div>
                    <div className="wrapper">
                        <input 
                            name="code"
                            onChange={handleInput}
                        />
                        <button className="btn_type1" onClick={checkCode}>Ïù∏Ï¶ùÎ≤àÌò∏ ÌôïÏù∏</button>        
                    </div>
                    <h6>{(isCodeChecked) ? 'Ïù∏Ï¶ùÎ≤àÌò∏Í∞Ä ÌôïÏù∏ÎêòÏóàÏñ¥Ïöî' : ''}</h6>
                </div>

                <div className="container">
                    <div className="inputMessage">ÎπÑÎ∞ÄÎ≤àÌò∏</div>
                    <input 
                        type='password'
                        name="password"
                        value={initInput && password.length === 0 ? "" : password}
                        onChange={handleInput}
                    />
                    <h5>{(password.length!==0 && !isValidPassword) ? '8Í∏ÄÏûê Ïù¥ÏÉÅÏúºÎ°ú ÏÑ§Ï†ïÌï¥ Ï£ºÏÑ∏Ïöîüò¢' : ''}</h5>
                        
                    <div className="inputMessage">ÎπÑÎ∞ÄÎ≤àÌò∏ ÌôïÏù∏</div>
                    <input 
                        type='password'
                        name="passwordConfirm"
                        value={initInput && passwordConfirm.length === 0 ? "" : passwordConfirm}
                        onChange={handleInput}
                    />
                    <h5>{(passwordConfirm.length!==0 && !isPasswordChecked) ? 'ÎπÑÎ∞ÄÎ≤àÌò∏Í∞Ä ÏùºÏπòÌïòÏßÄ ÏïäÏïÑÏöîüò¢' : ''}</h5>
                </div>
            </div>
            <button className="btn_type2" onClick={onSubmitHandler}>ÌôïÏù∏</button>
        </div>
    )
}

export default SignUp;
