import { useState } from "react";
import "./Search.css";
import SearchBar from "./SearchBar";
import axios from "axios";
import { getCookie } from "../cookie";
import FriendItem from "./friend/FriendItem";

const SEARCH_MEMBER = `https://mapdagu.site/api/friends?search`;
const SEARCH_FOOD = `https://mapdagu.site/api/food?search`;

const Search = ({isFood}) => {
    const accessToken = getCookie("accessToken");
    const [data, setData] = useState();
    const [isSubmit, setIsSubmit] = useState(false);
    const onSubmit = async(contents) => {
        try{
            if(isFood){
                axios.get([SEARCH_FOOD, contents].join("="), {headers: {Authorization: accessToken}})
                .then(res => {
                    setData(res.data.content.map((item) => ({
                        ...item
                    })));
                })
            }
            else{
                axios.get([SEARCH_MEMBER, contents].join("="), {headers: {Authorization: accessToken}})
                .then(res => {
                    setData(res.data.content.map((item) => ({
                        ...item
                    })));
                })
            }
            setIsSubmit(true);
        } catch(error){
            alert(error.response.data.message);
        }
    }
    if(!data){
        <div className="Search">
            <div className="contents_top">
                <SearchBar onSubmit={onSubmit}/>
            </div>
            <div className="contents_top">
                <div className="left">
                    <h1>최근 검색어</h1>
                </div>
                <div className="right">
                    <button className="btn_delete">전체 삭제</button>
                </div>
            </div>
            <div className="list_wrapper">
            {/* 최근 검색어 리스트    */}
            </div>
        </div>        
    }
    return(
        <div className="Search">
            <div className="contents_top">
                <SearchBar onSubmit={onSubmit}/>
            </div>
            {isSubmit ? "" :
            <div className="contents_top">
                <div className="left">
                    <h1>최근 검색어</h1>
                </div>
                <div className="right">
                    <button className="btn_delete">전체 삭제</button>
                </div>
            </div>
            }
            <div className="list_wrapper">
                { !data || data.length === 0 ?
                "" :
                data.map((it) => (                            
                    <FriendItem
                        key={it.id}
                            {...it}
                    />
                ))}
            </div>
        </div>
    );
}

export default Search;
