import "./selectionItem.css";
import React from "react";

const selectionItem = ({id, name, onClick, isSelected}) => {
    const handleOnClick = () => {
        onClick(id);
    }
    return(
        <div
            className = {[
                "selectionItem",
                isSelected ? `selectionItem_on_${id}` : `selectionItem_off`,
            ].join(" ")}
            onClick={handleOnClick}>
            <span>{name}</span>
        </div>
    );
}

export default React.memo(selectionItem);
