import "./Login.css";
import Header from "./Header";
import Button from "./Button";
import { useState } from "react";
import { useNavigate } from "react-router-dom";

const Login = ({onSubmit}) => {   
    const navigate = useNavigate();
    const [state, setState] = useState({
        id: "",
        password: "",
    });

    const REST_API_KEY = `4706c891bdcc562689b02b53f81f9891`;
    const REDIRECT_URI = `http://localhost:3000/oauth/callback/kakao`;
    const kakaoLink = `https://kauth.kakao.com/oauth/authorize?client_id=${REST_API_KEY}&redirect_uri=${REDIRECT_URI}&response_type=code`;
    
    const handleIdChange = (e) => {
        setState({
            ...state,
            id: e.target.value,
        });
    };
    const handlePasswordChange = (e) => {
        setState({
            ...state,
            password: e.target.value,
        })
    };
    const handleSubmit = () => {  
        // console.log(`${state.id}, ${state.password}`)
        onSubmit(state.id, state.password);
    }    
    const onSubscribe = () => {
        navigate(`/subscribe`);
    }
    const kakaoLoginHandler = () => {
        window.location.href = kakaoLink;
    }
    return(
        <div className="Login">            
            <Header title={`login page`}/>
            <div>로그인</div>
            <div>
                <textarea value={state.id} onChange={handleIdChange} placeholder="아이디" />
                <Button text="로그인" onClick={handleSubmit}/>
            </div> 
            <div><textarea value={state.password} onChange={handlePasswordChange} placeholder="비밀번호" /></div>
            <button>아이디 찾기</button>|
            <button>비밀번호 찾기</button>|
            <button onClick={onSubscribe}>회원가입</button>
            <div>
                <Button text="kakao" onClick={kakaoLoginHandler}/>
                <Button text="naver"/>
                <Button text="google"/>
            </div>
        </div>
    );
}

export default Login;
