import "./OptionItem.css";
import React from "react";

const OptionItem = ({id, img, name, onClick, isSelected}) => {
    const handleOnClick = () => {
        onClick(id);
    }
    return(
        <div 
            className = {[
                "OptionItem",
                isSelected ? `OptionItem_on_${id}` : `OptionItem_off`,
            ].join(" ")}
            onClick={handleOnClick}>
            <img alt={`emotion${id}`} src={img} />
            <span>{name}</span>
        </div>
    );
}

export default React.memo(OptionItem);
