import "./FoodItem.css";
import { useNavigate } from "react-router-dom";

const FoodItem = ({ id, name, image, scoville, isModal, onClickItem}) => {
    const navigate = useNavigate();

    const onClickHandler = () => {
        if(isModal){
            onClick(name, image);
        }else{
            navigate(`/detail/${id}`);
        }
    }

    return(
        <div className="FoodItem">
            <div onClick={onClickItem} className="image_section">
                <img alt="img_food" src={image}/>
            </div>
            <div onClick={onClickItem} className="text_section">
                <div className="text_itemName">{name}</div>
                <div className="text_scoville">스코빌지수: {scoville}</div>
            </div>
        </div>
    );
}

export default FoodItem;
