        {id: 9, text: "9"},
        {id: 10, text: "10"},
        {id: 11, text: "11"},
        {id: 12, text: "12"},
    ]
    const [focusedButton, setFocusedButton] = useState(null);
  
    const handleButtonFocus = (buttonId) => {
      setFocusedButton(buttonId);
    };
    if(level){
