    }
    return(
        <div className="selectionItem" onClick={handleOnClick}>
            <img alt={""} src={img}/>
            <span>{name}</span>
            {/* // className = {[
                "selectionItem",
                isSelected ? `selectionItem_on_${id}` : `selectionItem_off`,
            ].join(" ")}
            onClick={handleOnClick}>
            <span>{name}</span> */}
        </div>
    );
}

export default selectionItem;
