import "./SelectionItem.css";
import React from "react";

const SelectionItem = ({id, img, name, onClick, isSelected}) => {
    const handleOnClick = () => {
        onClick(id);
    }
    return(
        <div className="SelectionItem" onClick={handleOnClick}>
            <img alt={""} src={img}/>
            <span>{name}</span>
            {/* // className = {[
                "SelectionItem",
                isSelected ? `SelectionItem_on_${id}` : `SelectionItem_off`,
            ].join(" ")}
            onClick={handleOnClick}>
            <span>{name}</span> */}
        </div>
    );
}

export default SelectionItem;
