import Header from "../components/Header";
import Button from "../components/Button";
import { useNavigate } from "react-router-dom";
import { useState } from "react";

const EditProfile = () => {
    const navigate = useNavigate();

    const [inputValue, setInputValue] = useState({
        nickname: "",
        password: "",
        passwordConfirm: "",
    });

    const isValidNickname = nickname >= 2;
    const isValidPassword = password.length >= 8;
    const isPasswordChecked = ((password === passwordConfirm) ? true : false);
    const getIsActive = isValidNickname && isValidPassword && isPasswordChecked;

    const handleInput = (e) => {
        const { name, value } = e.target;
        setInputValue({
            ...inputValue,
            [name]: value,
        });
    };

    const handleOnBack = () => {
        navigate(-1);
    }
    const handleOnSubmit = () => {
        if(getIsActive){
            alert("변경되었습니다");
            navigate(`/mypage`);
        }
    }
    return (
        <div>
            <Header title="edit profile"
                    leftChild={<Button text="<" onClick={handleOnBack}/>}
                    rightChild={<Button text="확인" onClick={handleOnSubmit}/>}
            />
            <div>닉네임</div>
            <input name="nickname" onChange={handleInput}/>
            <div>비밀번호 변경</div>
            <input type="password" name="password" onChange={handleInput}/>
            <div>비밀번호 확인</div>
            <input type = "password" name="passwordConfirm" onChange={handleInput}/>
        </div>
    );
}

export default EditProfile;
