import "./SignUp.css";
import Button from "./Button";
import { useState } from "react";
import { useNavigate } from "react-router-dom";

const SignUp = () => {
    const navigate = useNavigate();
    const [confirm, setConfirm] =  useState();
    const [inputValue, setInputValue] = useState({
        userName: "",
        email: "",
        code: "",
        password: "",
        isCodeChecked: true, //수정필요
        isPasswordChecked: true, //수정필요
    });
    //이메일 검사: '@', '.'이 둘 다 포함 될 것
    const isValidEmail = email.includes('@') && email.includes('.');
    // 비밀번호 특수문자 검사를 위한 정규식 표현.
    const specialLetter = password.search(/[`~!@@#$%^&*|₩₩₩'₩";:₩/?]/gi);
    // 특수문자 1자 이상, 전체 8자 이상일 것.
    const isValidPassword = password.length >= 8 && specialLetter >= 1;
    // 모든 input의 value가 1자 이상이 될 것
    const isValidInput = userName.length >= 1 ;

    // 검사한 모든 로직의 유효성 검사가 true가 될때 getIsActive함수가 작동한다. 버튼 클릭 이벤트가 발생할때 넣어줄 함수.
    const getIsActive = 
    isValidEmail && isValidPassword && isValidInput === true;

    const { userName, email, code, password, isCodeChecked, isPasswordChecked } = inputValue;

    const handleInput = (e) => {
        const { name, value } = e.target;
        setInputValue({
            ...inputValue,
            [name]: value,
        });
    };
    // const handleOnChangeName = (e) => {
    //     setInputValue({
    //         ...inputValue,
    //         name: e.ta -rget.value,
    //     });
    // }
    // const handleOnChangeEmail = (e) => {
    //     setInputValue({
    //         ...inputValue,
    //         email: e.target.value,
    //     });
    // }
    // const handleOnChangeCode = (e) => {
    //     setInputValue({
    //         ...inputValue,
    //         code: e.target.value,
    //     });
    // }
    // const handleOnChangePassword = (e) => {
    //     setInputValue({
    //         ...inputValue,
    //         password: e.target.value,
    //     });
    // }
    // const handleOnChangePassword2 = (e) => {
    //         setConfirm(e.target.value);
    //     }

    // const onRequestCode = () => {
    //     alert("인증번호를 전송했습니다");
    // }
    const onCheckCode = () => {
        /*
        if(코드일치)
            확인되었습니다
            setInputValue({
                ...inputValue,
                isCodeChecked: true,
            })
        else{
            틀렸습니다
            setInputValue({
                ...inputValue,
                iscodeChecked: false,
            })
        }
        */
    }    
    
    const onSubmit = () => {
        // if(state.password === confirm){
        //     setInputValue({
        //         ...inputValue,
        //         isPasswordChecked: true,
        //     })
        // }
        // else{
        //     setInputValue({
        //         ...inputValue,
        //         isPasswordChecked: false,
        //     })
        // }

        if(userName === ""){
            alert("이름을 입력하세요");
        }
        else if(!isCodeChecked){
            alert("이메일 인증을 해주세요");
        }
        else if(!isPasswordChecked){
            alert("비밀번호가 같지 않습니다")
        }
        else
            navigate(`/set_profile`);
    }

    return (
        <div className="SignUp">
            <form className="signUpInput">
                <div className="nameInput">
                    <div className="inputMessage">1. 이름</div>
                    <input 
                        name="userName"
                        onChange={handleInput}
                    />
                </div>
                {/* <div>
                    <textarea value={state.name} onChange={handleOnChangeName} placeholder="이름 입력" />
                </div> */}
                <div className="emailInput">
                    <div className="inputMessage">2. 이메일 인증</div>
                    <input 
                        name="email"
                        onChange={handleInput}
                    />
                </div>
                {/* <div>
                    <textarea value={state.email} onChange={handleOnChangeEmail} placeholder="이메일 입력" />
                    <Button text="인증번호 요청" onClick={onRequestCode}/>
                </div>
                <div>
                    <textarea value={state.code} onChange={handleOnChangeCode} placeholder="인증번호 입력" />
                    <Button text="인증번호 확인" onClick={onCheckCode}/>
                </div> */}
                <div className="passwordInput">
                    <div className="inputMessage">3. 비밀번호 설정</div>
                    <input 
                        name="password"
                        onChange={handleInput}
                    />
                </div>
                {/* <div>
                    <textarea value={state.password} onChange={handleOnChangePassword} placeholder="비밀번호 설정" />
                </div>
                <div>    
                    <textarea value={confirm} onChange={handleOnChangePassword2} placeholder="비밀번호 확인" />
                </div> */}                
            </form>
            <Button text="다음 단계" onClick={onSubmit}/>
        </div>
    )
}

export default SignUp;
