import Header from "../components/Header";
import Selection from "../components/Selection";
import { useNavigate } from "react-router-dom";
import { useContext, useRef, useState } from "react";
import { EvalDispatchContext, EvalStateContext } from "../App";

const Test = ({maxTestNum, isFirst}) => {    
    const data = useContext(EvalStateContext);
    const {onCreate, onUpdate} = useContext(EvalDispatchContext);
    const [ testNum, setTestNum ] = useState(0);
    const navigate = useNavigate();
    const idRef = useRef(3);
    const matchItem = data.find((it)=> String(it.id) === String(idRef.current));
   
    const goNext = (data) => {
        const { date, itemName, selectionId } = data;
        if(selectionId === 0){
            alert("하나를 선택해주세요");
        }
        else{
            if(!matchItem){
                onCreate( date, itemName, selectionId );
                idRef.current += 1;
            }
            else{
                onUpdate( idRef.current, date, itemName, selectionId );
                idRef.current += 1;
            }
            if(testNum === maxTestNum){
                navigate(`/result`);
                isFirst = false;
            }
            else{
                setTestNum(testNum + 1);
            }
        }
    }
    const goBack = () => {
        if(testNum === 0){
            navigate(`/`);
        }
        else{
            setTestNum(testNum - 1);
        }
    }
    return (
        <div>
            <Header title={`테스트 페이지`}/>
            {testNum+1}/{maxTestNum+1}
            <Selection testNum={testNum} goNext={goNext} goBack={goBack}/>
        </div>
    )
}

export default Test;
