import Header from "../components/Header";
import Button from "../components/Button";
import Editor from "../components/Editor";
import useDetail from "../hooks/useDetail";
import { useNavigate, useParams } from "react-router-dom";
import { EvalDispatchContext } from "../App";
import { useContext, useState } from "react";
import axios from "axios";
import { useEffect } from "react";

const SERVER_URL = `https://mapdagu.site/api/evaluations`;

const EditEval = ({accessToken}) => {    
    // console.log([SERVER_URL, id].join("/"));
    const navigate = useNavigate();
    const { id } = useParams();
    const data = useDetail(id);
    const {onUpdate, onDelete} = useContext(EvalDispatchContext);
    const [newData, setNewData] = useState({
        name: "",
        imageNum: 0,
        score: 0,
    });

    useEffect(() => {
        try{
            axios.get([SERVER_URL, 4].join("/"), {headers: {Authorization: accessToken}})
                .then(res => {
                    console.log(res.data);
                    setNewData({
                        // name: res.data.name,
                        // imageNum: res.data.imageNum,
                        // score: res.data.score,
                        name: "a",
                        imageNum: 1,
                        score: 2,
                    });
                    console.log(newData);
                })
        } catch(error){
            alert(error.response.data.message);
        }
    }, []);
    

    const handleOnBack = () => {
        navigate(-1);
    }

    const onSubmit = (data) => {
      if(window.confirm("평가를 정말 수정할까요?")){
            const { date, itemName, selectionId } = data;
            onUpdate(id, date, itemName, selectionId);
            navigate(`/evaluate`, {replace:true});
        }
    }

    const onClickDelete = () => {
        if(window.confirm("평가를 정말 삭제할까요?")){
            onDelete(id);
            navigate(`/evaluate`, {replace:true});
        }
    }
    
    if(!data){
        return (<div>항목을 불러오고 있습니다...</div>)
    }else{
        return(
            <div>
                <Header
                    title="edit page"
                    leftChild={<Button text="←" onClick={handleOnBack}/>}
                    rightChild={<Button text="삭제하기" onClick={onClickDelete}/>}
                />
                <Editor initData={newData} onSubmit={onSubmit}/>
            </div>
        );
    }
    
}

export default EditEval;
