import Graph from "../Graph";
import "./FriendViewer.css";
import icon_noti from "../../img/icon/friend_notification.png";
import icon_noti_on from "../../img/icon/friend_notification_on.png";
import FriendItem from "./FriendItem";
import axios from "axios";
import { useEffect, useState } from "react";
import { getCookie } from "../../cookie";

const GET_LIST = `https://mapdagu.site/api/friends/request`;

const FriendViewer = () => {
    const isNotiOn = true;
    // const data = [
    //     {
    //         id: 0,
    //         imageNum: 4,
    //         userName: "닉네임",
    //         level: 3,
    //     },
    //     {
    //         id: 1,
    //         imageNum: 7,
    //         userName: "닉네임",
    //         level: 4,
    //     },
    //     {
    //         id: 2,
    //         imageNum: 3,
    //         userName: "닉네임",
    //         level: 5,
    //     },
    //     {
    //         id: 3,
    //         imageNum: 4,
    //         userName: "닉네임",
    //         level: 3,
    //     },
    //     {
    //         id: 4,
    //         imageNum: 7,
    //         userName: "닉네임",
    //         level: 4,
    //     },
    //     {
    //         id: 5,
    //         imageNum: 3,
    //         userName: "닉네임",
    //         level: 5,
    //     },
    //     {
    //         id: 6,
    //         imageNum: 4,
    //         userName: "닉네임",
    //         level: 3,
    //     },
    //     {
    //         id: 7,
    //         imageNum: 7,
    //         userName: "닉네임",
    //         level: 4,
    //     },
    //     {
    //         id: 8,
    //         imageNum: 3,
    //         userName: "닉네임",
    //         level: 5,
    //     }
    // ]    
    const [data, setData] = useState();
    const accessToken = getCookie("accessToken");

    useEffect(() => {
        try{
            axios.get(GET_LIST, {headers: {Authorization: accessToken}})
            .then(res => {
                setData(res.data.content.map((item) => ({
                    // id: index,
                    ...item
                })));
            })
        } catch(error){
            alert(error.response.data.message);
        }
    }, [])

    return (
        <div className="FriendViewer">
            <div className="contents_top">
                <div className="text_title">친구 목록</div>
                <img className="img_noti" alt="noti" src={isNotiOn ? icon_noti_on : icon_noti}/>
            </div>
            <div className="graph_wrapper">
                <Graph level = {1}/>
            </div>
            <div className="list_wrapper">
                {data.map((it) => (
                    <FriendItem
                        key={it.id}
                        {...it}
                        // maxTestNum={maxTestNum}
                    />
                ))}
            </div>
        </div>
    )
}

export default FriendViewer;
