import Header from "../components/Header";
import Button from "../components/Button";
import { useNavigate } from "react-router-dom";
import { useState } from "react";

const EditProfile = () => {
    const navigate = useNavigate();

    const [inputValue, setInputValue] = useState({
        nickname: "",
        introduction: "",
        password: "",
        passwordConfirm: "",
    });
    const { nickname, introduction, password, passwordConfirm } = inputValue;

    const isValidNickname = nickname.length >= 2 && nickname <= 5;
    const isValidPassword = password.length >= 8;
    const getIsActive = isValidNickname && isValidPassword;

    const handleInput = (e) => {
        const { name, value } = e.target;
        setInputValue({
            ...inputValue,
            [name]: value,
        });
    };

    const handleOnBack = () => {
        navigate(-1);
    }
    const handleOnSubmit = () => {
        if(getIsActive){
            alert("변경되었습니다");
            navigate(`/mypage`);
        }
    }
    return (
        <div>
            <Header title="edit profile"
                    leftChild={<Button text="취소" onClick={handleOnBack}/>}
                    rightChild={<Button text="완료" onClick={handleOnSubmit}/>}
            />
            <div>이메일</div>
            <input type="email"/>            
            <div>닉네임</div>
            <input name="nickname" onChange={handleInput}/>
            <h5>{(nickname.length!=0 && !isValidNickname) ? '2글자 이상 5글자 이하로 입력하세요' : ''}</h5>
            <div>한줄소개</div>
            <input name="introduction"/>
        </div>
    );
}

export default EditProfile;
